import React, { useEffect } from 'react';
import portfolios from '../../assets/data/portfolioData';

const Modal = ({ activeID, setShowModal }) => {
  const portfolio = portfolios.find((portfolio) => portfolio.id === activeID);

  // Menutup modal ketika mengklik di luar modal
  const handleCloseModal = (e) => {
    if (e.target.classList.contains('modal-overlay')) {
      setShowModal(false);
    }
  };

  

  return (
    <div className="fixed inset-0 z-10 flex items-center justify-center modal-overlay">
      <div className="max-w-[600px] w-full mx-4 bg-white rounded-lg p-5 relative">
        <span
          className="absolute top-2 right-2 cursor-pointer text-red-600 text-2xl"
          onClick={() => setShowModal(false)}
        >
          <i className="ri-close-fill"></i>
        </span>
        <div>
          <figure>
            <img src={portfolio?.imgUrl} alt="" />
          </figure>
        </div>
      </div>
    </div>
  );
};

export default Modal;
