import React, { useEffect, use } from "react";
import portfolios from "../../assets/data/portfolioData";

const Modal = ({ activeID, setShowModal }) => {
  const portfolio = portfolios.find((portfolio) => portfolio.id === activeID);

  // State untuk melacak apakah modal terbuka atau tidak
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Menutup modal ketika mengklik di luar modal
  const handleCloseModal = (e) => {
    if (e.target.classList.contains("modal-overlay")) {
      closeAndUnlockScroll();
    }
  };

  // Mengunci scrolling
  const lockScroll = () => {
    document.body.classList.add("modal-open");
  };

  // Membuka scrolling
  const unlockScroll = () => {
    document.body.classList.remove("modal-open");
  };

  // Fungsi untuk menampilkan modal
  const openModal = () => {
    setIsModalOpen(true);
    lockScroll(); // Mengunci scrolling saat modal terbuka
  };

  // Fungsi untuk menutup modal dan membuka scrolling
  const closeAndUnlockScroll = () => {
    setIsModalOpen(false);
    unlockScroll(); // Membuka scrolling saat modal tertutup
  };

  useEffect(() => {
    if (isModalOpen) {
      openModal();
    }
  }, [isModalOpen]);

  return (
    <div
      className={`fixed inset-0 z-10 flex items-center justify-center modal-overlay ${
        isModalOpen ? "bg-opacity-80" : ""
      }`}
      onClick={handleCloseModal}
    >
      <div className="max-w-[600px] w-full mx-4 bg-white rounded-lg p-5 relative">
        <span
          className="absolute top-2 right-2 cursor-pointer"
          onClick={() => closeAndUnlockScroll()} // Menutup modal dan membuka scrolling saat mengklik icon X
        >
          <div className="w-[35px] h-[35px] bg-alertColor text-white text-[16px] font-[600] rounded-full flex items-center justify-center">
            <i className="ri-close-fill"></i>
          </div>
        </span>
        <div>
          <figure>
            <img src={portfolio?.imgUrl} alt="" />
          </figure>
        </div>
        {/* Isi konten modal di sini */}
        <h2 className="text-xl font-bold mb-3">Judul Konten</h2>
        <p className="text-gray-600">Deskripsi konten modal.</p>
        {/* Contoh tombol untuk menutup modal */}
        <button
          onClick={() => closeAndUnlockScroll()} // Menutup modal dan membuka scrolling saat mengklik tombol
          className="bg-primaryColor text-white py-2 px-4 rounded-lg mt-4 hover:bg-primaryColor-dark"
        >
          Tutup Modal
        </button>
      </div>
    </div>
  );
};

export default Modal;
